USE AdventureWorks2017;-- Question 1SELECT * FROM Production.ProductSELECT * FROM Production.ProductReviewSELECT Name, ProductReviewID, Color	FROM Production.ProductReview PR LEFT JOIN Production.Product P	ON     PR.ProductID = P.ProductID	WHERE Color NOT IN ('Multi','Yellow');-- Question 2SELECT * FROM Sales.SalesTerritory;SELECT * FROM Sales.SalesPerson SP;SELECT Name, BusinessEntityID, ST.SalesYTD	FROM Sales.SalesTerritory ST LEFT JOIN Sales.SalesPerson SP	ON     ST.TerritoryID = SP.TerritoryID	WHERE ST.SalesYTD > 1000000 AND CountryRegionCode = 'US' AND CommissionPct < 0.10;-- Question 3SELECT * FROM Person.Person;SELECT Name, FirstName, LastName, SP.BusinessEntityID, ST.SalesYTD	FROM Sales.SalesTerritory ST LEFT JOIN Sales.SalesPerson SP 	ON     ST.TerritoryID = SP.TerritoryID		LEFT JOIN Person.Person P		ON SP.BusinessEntityID = P.BusinessEntityIDWHERE ST.SalesYTD > 1000000 AND CountryRegionCode = 'US' AND CommissionPct < 0.10;-- Question 4SELECT * FROM HumanResources.Employee;SELECT TOP 15 P.FirstName, P.LastName, E.JobTitle, E.NationalIDNumber	FROM HumanResources.Employee E JOIN Person.Person P	ON e.BusinessEntityID = p.BusinessEntityID	ORDER By 1;